document.getElementById('checkoutForm').addEventListener('submit',function(e){e.preventDefault();const n=document.getElementById('nomeCarta').value.trim(),c=document.getElementById('numeroCarta').value.trim(),s=document.getElementById('scadenzaCarta').value.trim(),v=document.getElementById('cvv').value.trim(),m=document.getElementById('error-message');if(!/^[a-zA-Z\s]+$/.test(n)){m.textContent="Il nome sulla carta non è valido. Inserisci solo lettere e spazi.";m.style.display='block';return}if(!/^\d{16}$/.test(c)){m.textContent="Il numero della carta deve contenere esattamente 16 cifre.";m.style.display='block';return}if(!/^(0[1-9]|1[0-2])\/\d{2}$/.test(s)){m.textContent="La data di scadenza non è valida. Usa il formato MM/AA.";m.style.display='block';return}if(!/^\d{3}$/.test(v)){m.textContent="Il CVV deve contenere esattamente 3 cifre.";m.style.display='block';return}m.style.display='none';this.submit()});

